// packages/config/src/env.ts
import 'dotenv/config';
import { z } from 'zod';

/**
 * Centralized runtime environment schema validation.
 * Ensures all required environment variables are present and typed correctly.
 */
const EnvSchema = z.object({
  // Runtime
  NODE_ENV: z.enum(['development', 'test', 'production']).default('development'),
  PORT: z.coerce.number().int().positive().default(3000),

  // Database
  DATABASE_URL: z.string().url(),

  // Auth (Okta / NextAuth.js)
  NEXTAUTH_URL: z.string().url(),
  NEXTAUTH_SECRET: z.string().min(32),
  OKTA_CLIENT_ID: z.string(),
  OKTA_CLIENT_SECRET: z.string(),
  OKTA_ISSUER: z.string().url(),

  // Redis
  REDIS_URL: z.string().url(),

  // S3 / Storage
  S3_ENDPOINT: z.string().url().optional(),
  S3_REGION: z.string(),
  S3_BUCKET: z.string(),
  S3_ACCESS_KEY_ID: z.string(),
  S3_SECRET_ACCESS_KEY: z.string(),

  // Email
  EMAIL_FROM: z.string().email(),
  SMTP_HOST: z.string(),
  SMTP_PORT: z.coerce.number().int().positive(),
  SMTP_USER: z.string(),
  SMTP_PASS: z.string(),

  // Observability
  OTEL_EXPORTER_OTLP_ENDPOINT: z.string().url().optional(),
  OTEL_SERVICE_NAME: z.string().default('bowdoin-marketplace'),

  // Feature Flags
  FEATURE_FLAGS: z.string().optional() // e.g. "newMessaging,brunswickUsers"
});

const parsed = EnvSchema.safeParse(process.env);

if (!parsed.success) {
  console.error('âŒ Invalid environment configuration:', parsed.error.flatten().fieldErrors);
  throw new Error('Invalid environment variables');
}

export const env = parsed.data;

/**
 * Helper: parse feature flags into a Set for O(1) checks
 */
export const featureFlags = new Set(
  env.FEATURE_FLAGS ? env.FEATURE_FLAGS.split(',').map((f) => f.trim()) : []
);

// packages/config/src/flags.ts
import { env, featureFlags } from './env';

/**
 * Centralized feature-flag definitions.
 * - Add new flags to FlagName and DEFAULTS.
 * - Flags can be enabled by:
 *    1) DEFAULTS (per-environment)
 *    2) FEATURE_FLAGS env list (comma-separated)
 *    3) FLAG_<NAME>=true env override (highest precedence)
 * - Supports deterministic percentage rollouts and variant bucketing.
 */

export type FlagName =
  | 'brunswickUsers'          // allow non-Bowdoin community accounts & UI affordances
  | 'communitySignup'         // self-serve email verification flow for residents
  | 'emailVerification'       // email verification requirement for non-SAML accounts
  | 'imagePipelineV2'         // new sharp pipeline / quality tuning
  | 'ftsSearchV2'             // experimental search tuning
  | 'sseMessaging'            // server-sent events for messages
  | 'pwaInstallPrompt'        // show PWA install prompt hints
  | 'adminImpersonation';     // admin-only: impersonate user for support

type FlagDefaults = Partial<Record<FlagName, boolean>>;

const DEFAULTS_BY_ENV: Record<typeof env.NODE_ENV, FlagDefaults> = {
  development: {
    brunswickUsers: true,
    communitySignup: true,
    emailVerification: true,
    imagePipelineV2: true,
    ftsSearchV2: true,
    sseMessaging: true,
    pwaInstallPrompt: false,
    adminImpersonation: true,
  },
  test: {
    brunswickUsers: true,
    communitySignup: true,
    emailVerification: true,
    imagePipelineV2: false,
    ftsSearchV2: false,
    sseMessaging: false,
    pwaInstallPrompt: false,
    adminImpersonation: true,
  },
  production: {
    brunswickUsers: true,
    communitySignup: true,
    emailVerification: true,
    imagePipelineV2: true,
    ftsSearchV2: false,
    sseMessaging: true,
    pwaInstallPrompt: true,
    adminImpersonation: false,
  },
};

const DEFAULTS: FlagDefaults = DEFAULTS_BY_ENV[env.NODE_ENV] ?? {};

/** Highest-precedence per-flag boolean override: FLAG_<UPPERCASE_NAME>=true/false */
function envOverride(name: FlagName): boolean | undefined {
  const key = `FLAG_${name.replace(/[^a-zA-Z0-9]/g, '_').toUpperCase()}`;
  const raw = process.env[key];
  if (raw == null) return undefined;
  if (/^(1|true|yes|on)$/i.test(raw)) return true;
  if (/^(0|false|no|off)$/i.test(raw)) return false;
  return undefined;
}

/** Simple, stable hash for bucketing (djb2) */
function djb2(input: string): number {
  let hash = 5381;
  for (let i = 0; i < input.length; i++) {
    // hash * 33 + char
    // eslint-disable-next-line no-bitwise
    hash = ((hash << 5) + hash) + input.charCodeAt(i);
    // eslint-disable-next-line no-bitwise
    hash &= 0xffffffff;
  }
  // normalize to [0, 4294967295]
  return hash >>> 0;
}

export type AudienceKey = { userId?: string; email?: string };

/**
 * Determine if a flag is enabled.
 * - precedence: envOverride -> FEATURE_FLAGS -> DEFAULTS
 * - if percent is provided, applies deterministic bucketing using audienceKey
 */
export function isEnabled(
  name: FlagName,
  options?: {
    /** Percent rollout 0..100 (applied only if base flag resolves to true) */
    percent?: number;
    /** Stable key for bucketing (userId preferred, fallback to email) */
    audienceKey?: AudienceKey;
  }
): boolean {
  // 1) Explicit env override
  const override = envOverride(name);
  if (override !== undefined) {
    return applyPercent(override, options);
  }

  // 2) FEATURE_FLAGS (comma-separated) from env.ts -> Set
  if (featureFlags.has(name)) {
    return applyPercent(true, options);
  }

  // 3) Defaults per env
  const base = DEFAULTS[name] ?? false;
  return applyPercent(base, options);
}

function applyPercent(base: boolean, options?: { percent?: number; audienceKey?: AudienceKey }) {
  if (!base) return false;
  const pct = options?.percent;
  if (pct == null || pct >= 100) return true;
  if (pct <= 0) return false;
  const id = options?.audienceKey?.userId ?? options?.audienceKey?.email ?? 'anonymous';
  const bucket = djb2(id + '::' + pct) % 100; // 0..99
  return bucket < Math.floor(pct);
}

/**
 * Deterministically choose a variant for A/B/n testing.
 * Example: getVariant('ftsSearchV2', ['baseline','synonyms','trigram'], { audienceKey:{userId} })
 */
export function getVariant(
  name: FlagName,
  variants: string[],
  audienceKey?: AudienceKey
): string | undefined {
  if (!isEnabled(name)) return undefined;
  if (!variants.length) return undefined;
  const id = audienceKey?.userId ?? audienceKey?.email ?? 'anonymous';
  const idx = djb2(`${name}::${variants.join('|')}::${id}`) % variants.length;
  return variants[idx];
}

/** Snapshot of all flags as booleans (no percent), useful for diagnostics and logs */
export function allFlags(audienceKey?: AudienceKey): Record<FlagName, boolean> {
  const names = Object.keys(DEFAULTS) as FlagName[];
  // Include any FEATURE_FLAGS not in defaults (forward-compat).
  const extra = Array.from(featureFlags).filter((n): n is FlagName => (n as any) in DEFAULTS || true);

  const set = new Set<FlagName>([...names, ...extra]);
  const out: Record<FlagName, boolean> = {} as any;
  for (const n of set) {
    out[n] = isEnabled(n, { audienceKey });
  }
  return out;
}

/** Public list of declared flags (useful for admin UI) */
export const DECLARED_FLAGS = Object.freeze(
  (Object.keys(DEFAULTS) as FlagName[]).sort()
);
