/* eslint-disable no-console */
import { PrismaClient, Prisma } from '@prisma/client';
import { env } from '@bowdoin/config/env';
import { logger } from '@bowdoin/observability/logger';
import { getTracer } from '@bowdoin/observability/tracing';

const isProd = env.NODE_ENV === 'production';
const isTest = env.NODE_ENV === 'test';

/**
 * Prisma logging: in dev, surface query timing to console for quick diagnosis.
 * In prod, keep it lean; rely on OpenTelemetry spans + error logs.
 */
const prismaLog: Prisma.LogDefinition[] = isProd
  ? [{ level: 'error', emit: 'event' }, { level: 'warn', emit: 'event' }]
  : [
      { level: 'query', emit: 'event' },
      { level: 'info', emit: 'event' },
      { level: 'warn', emit: 'event' },
      { level: 'error', emit: 'event' },
    ];

/**
 * Singleton PrismaClient across hot-reloads:
 *  - Node.js re-creates module state on dev reloads; stash on globalThis.
 */
declare global {
  // eslint-disable-next-line no-var
  var __PRISMA__: PrismaClient | undefined;
}

function createPrisma(): PrismaClient {
  const client = new PrismaClient({
    log: prismaLog,
    errorFormat: isProd ? 'minimal' : 'colorless',
    datasources: env.DATABASE_URL ? { db: { url: env.DATABASE_URL } } : undefined,
  });

  // Bridge Prisma event logs -> pino
  prismaLog.forEach((def) => {
    if (def.emit !== 'event') return;

    client.$on(def.level as Prisma.LogLevel, (e) => {
      switch (def.level) {
        case 'query': {
          // Keep dev-only noisy logs out of prod
          if (!isProd) {
            // @ts-expect-error known Prisma event shape
            const { query, params, duration, target } = e;
            logger.debug(
              { query, params, duration, target, component: 'prisma' },
              'prisma.query',
            );
          }
          break;
        }
        case 'info':
          logger.info({ component: 'prisma', ...e }, 'prisma.info');
          break;
        case 'warn':
          logger.warn({ component: 'prisma', ...e }, 'prisma.warn');
          break;
        case 'error':
          logger.error({ component: 'prisma', ...e }, 'prisma.error');
          break;
        default:
          break;
      }
    });
  });

  // Optional: tie Prisma queries into OTEL spans (lightweight tagging)
  const tracer = getTracer();
  client.$use(async (params, next) => {
    return tracer.startActiveSpan(`prisma.${params.model ?? 'raw'}.${params.action}`, async (span) => {
      try {
        // Annotate common attributes
        if (params.model) span.setAttribute('db.sql.table', params.model);
        span.setAttribute('db.operation', params.action);
        const result = await next(params);
        return result;
      } catch (err) {
        span.recordException(err as Error);
        span.setStatus({ code: 2 }); // ERROR
        throw err;
      } finally {
        span.end();
      }
    });
  });

  return client;
}

// Reuse singleton in dev; fresh in prod/test per process
export const prisma: PrismaClient =
  globalThis.__PRISMA__ ?? createPrisma();

if (!isProd && !isTest) {
  globalThis.__PRISMA__ = prisma;
}

/**
 * Connect lazily; callers can await to ensure a live pool.
 */
export async function connectIfNeeded(): Promise<void> {
  // Prisma defers connection until first query; explicit connect aids readyz
  try {
    await prisma.$connect();
  } catch (err) {
    logger.error({ err }, 'db.connect.failed');
    throw err;
  }
}

/**
 * Health probe for /api/healthz and /api/readyz:
 * - round-trips with NOW() and version()
 */
export async function dbHealthCheck(): Promise<{
  ok: boolean;
  now?: string;
  version?: string;
  error?: string;
}> {
  try {
    // Minimal, cheap roundtrip
    const [{ now }] = await prisma.$queryRawUnsafe<Array<{ now: string }>>(
      'SELECT NOW()::text as now;',
    );
    const [{ version }] = await prisma.$queryRawUnsafe<Array<{ version: string }>>(
      'SELECT version();',
    );

    return { ok: true, now, version };
  } catch (err) {
    logger.error({ err }, 'db.health.failed');
    return { ok: false, error: (err as Error).message };
  }
}

/**
 * Transaction helper with correct typing.
 * Usage:
 *   await withTransaction(async (tx) => { await tx.user.create(...); })
 */
export async function withTransaction<T>(
  fn: (tx: Prisma.TransactionClient) => Promise<T>,
  options?: Parameters<PrismaClient['$transaction']>[1],
): Promise<T> {
  return prisma.$transaction(async (tx) => fn(tx), options);
}

/**
 * Graceful shutdown; call from process signal handlers in the web/worker.
 */
export async function shutdownPrisma(): Promise<void> {
  try {
    await prisma.$disconnect();
  } catch (err) {
    logger.warn({ err }, 'db.disconnect.warn');
  }
}

// Convenience re-exports for consumers that want types
export type { PrismaClient } from '@prisma/client';
export type { Prisma as PrismaTypes } from '@prisma/client';

// packages/db/src/migrate.ts
/**
 * Bowdoin Marketplace â€” Prisma migration runner
 *
 * Safe, production-minded wrapper over Prisma CLI for:
 *  - deploy:  prisma migrate deploy     (production-safe)
 *  - status:  prisma migrate status     (introspection only)
 *  - generate:prisma generate           (generate client)
 *  - reset:   prisma migrate reset      (DEV ONLY unless FORCE=1)
 *  - push:    prisma db push            (DEV ONLY unless FORCE=1)
 *
 * Usage:
 *   pnpm --filter @bowdoin/db exec ts-node src/migrate.ts deploy
 *   pnpm --filter @bowdoin/db exec ts-node src/migrate.ts status
 *   pnpm --filter @bowdoin/db exec ts-node src/migrate.ts generate
 *   pnpm --filter @bowdoin/db exec ts-node src/migrate.ts reset
 *   pnpm --filter @bowdoin/db exec ts-node src/migrate.ts push
 */

import { spawn } from 'node:child_process';
import { fileURLToPath, pathToFileURL } from 'node:url';
import { dirname, resolve } from 'node:path';

import { env } from '@bowdoin/config/env';
import { logger } from '@bowdoin/observability/logger';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

// Ensure we run prisma relative to this package so it picks up prisma/schema.prisma
const DB_PKG_DIR = resolve(__dirname, '..');
const PRISMA_SCHEMA = resolve(DB_PKG_DIR, 'prisma', 'schema.prisma');

type Command = 'deploy' | 'status' | 'generate' | 'reset' | 'push';

const isProd = env.NODE_ENV === 'production';
const FORCE = process.env.FORCE === '1';

function ensureDatabaseUrl() {
  if (!env.DATABASE_URL) {
    const msg =
      'DATABASE_URL is not set. Aborting. Provide a valid Postgres connection string in env.';
    logger.error({ msg });
    process.stderr.write(`${msg}\n`);
    process.exit(2);
  }
}

function forbidInProdUnlessForced(cmd: Command) {
  if (isProd && (cmd === 'reset' || cmd === 'push') && !FORCE) {
    const msg = `Refusing to run '${cmd}' in production. Set FORCE=1 if you really intend to do this.`;
    logger.error({ msg, cmd, isProd, FORCE });
    process.stderr.write(`${msg}\n`);
    process.exit(3);
  }
}

function runPrisma(args: string[]): Promise<number> {
  return new Promise((resolveExit) => {
    const child = spawn(
      process.platform === 'win32' ? 'npx.cmd' : 'npx',
      ['-y', 'prisma', ...args, '--schema', PRISMA_SCHEMA],
      {
        cwd: DB_PKG_DIR,
        stdio: 'inherit',
        env: {
          ...process.env,
          DATABASE_URL: env.DATABASE_URL,
          NODE_ENV: env.NODE_ENV,
        },
      },
    );

    child.on('close', (code) => {
      resolveExit(code ?? 1);
    });
  });
}

async function main() {
  const [, , rawCmd] = process.argv;
  const cmd = (rawCmd as Command) || 'status';

  ensureDatabaseUrl();

  switch (cmd) {
    case 'deploy': {
      logger.info({ msg: 'Applying pending migrations (prisma migrate deploy)â€¦', schema: PRISMA_SCHEMA });
      const code = await runPrisma(['migrate', 'deploy']);
      if (code !== 0) process.exit(code);
      logger.info({ msg: 'Migrations deployed successfully.' });
      break;
    }

    case 'status': {
      logger.info({ msg: 'Checking migration status (prisma migrate status)â€¦' });
      const code = await runPrisma(['migrate', 'status']);
      if (code !== 0) process.exit(code);
      break;
    }

    case 'generate': {
      logger.info({ msg: 'Generating Prisma client (prisma generate)â€¦' });
      const code = await runPrisma(['generate']);
      if (code !== 0) process.exit(code);
      logger.info({ msg: 'Prisma client generated.' });
      break;
    }

    case 'reset': {
      forbidInProdUnlessForced(cmd);
      logger.warn({
        msg: 'RESETTING DATABASE (prisma migrate reset)â€¦ This WILL DROP all data.',
        FORCE,
      });
      const args = ['migrate', 'reset', '--force'];
      // Allow running seed after reset if you want: remove --skip-seed to enable seeding script
      if (process.env.SKIP_SEED === '1') args.push('--skip-seed');
      const code = await runPrisma(args);
      if (code !== 0) process.exit(code);
      logger.info({ msg: 'Database reset complete.' });
      break;
    }

    case 'push': {
      forbidInProdUnlessForced(cmd);
      logger.warn({
        msg: 'Applying schema changes with prisma db push (DEV ONLY). Prefer migrations for prod.',
        FORCE,
      });
      const code = await runPrisma(['db', 'push']);
      if (code !== 0) process.exit(code);
      logger.info({ msg: 'db push completed.' });
      break;
    }

    default: {
      const msg =
        `Unknown command: ${rawCmd}\n` +
        `Usage: ts-node src/migrate.ts <deploy|status|generate|reset|push>`;
      logger.error({ msg, rawCmd });
      process.stderr.write(`${msg}\n`);
      process.exit(1);
    }
  }
}

main().catch((err) => {
  logger.error({ msg: 'Migration runner crashed', err });
  process.stderr.write(`Migration error: ${(err as Error).message}\n`);
  process.exit(1);
});

